import React, { useState } from 'react';
import { MapContainer, TileLayer, ImageOverlay, useMapEvents, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Line } from 'react-chartjs-2';

// --- LEAFLET ICON FIX ---
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
let DefaultIcon = L.icon({ iconUrl: icon, shadowUrl: iconShadow, iconSize: [25, 41], iconAnchor: [12, 41] });
L.Marker.prototype.options.icon = DefaultIcon;

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

// --- FULL DATASET: 25 GLOBAL LOCATIONS ---
const COASTAL_POINTS = [
  { name: "Puri Beach, Odisha", lat: 19.798, lon: 85.824, type: "Erosion Risk" },
  { name: "Marine Drive, Mumbai", lat: 18.944, lon: 72.823, type: "Urban Coast" },
  { name: "Marina Beach, Chennai", lat: 13.050, lon: 80.282, type: "Sandy" },
  { name: "Varkala, Kerala", lat: 8.737, lon: 76.716, type: "Cliff" },
  { name: "Visakhapatnam", lat: 17.704, lon: 83.332, type: "Port City" },
  { name: "Lakshadweep", lat: 10.566, lon: 72.641, type: "Atoll" },
  { name: "Gold Coast, Australia", lat: -28.016, lon: 153.400, type: "High Energy" },
  { name: "Miami Beach, USA", lat: 25.790, lon: -80.130, type: "Flood Risk" },
  { name: "Maldives", lat: 3.202, lon: 73.220, type: "Critical Level" },
  { name: "Dubai, UAE", lat: 25.204, lon: 55.270, type: "Artificial" },
  { name: "Cancun, Mexico", lat: 21.161, lon: -86.851, type: "Tourism" },
  { name: "Copacabana, Brazil", lat: -22.969, lon: -43.182, type: "Urban" },
  { name: "Nice, France", lat: 43.696, lon: 7.265, type: "Pebble" },
  { name: "Phuket, Thailand", lat: 7.880, lon: 98.392, type: "Tropical" },
  { name: "Bali, Indonesia", lat: -8.409, lon: 115.188, type: "Volcanic" },
  { name: "Cape Town, SA", lat: -33.924, lon: 18.424, type: "Rocky" },
  { name: "Santorini, Greece", lat: 36.393, lon: 25.461, type: "Caldera" },
  { name: "Bora Bora", lat: -16.500, lon: -151.741, type: "Lagoon" },
  { name: "Bondi Beach, Aus", lat: -33.891, lon: 151.277, type: "Bay" },
  { name: "Outer Banks, USA", lat: 35.558, lon: -75.466, type: "Barrier" },
  { name: "Cornwall, UK", lat: 50.266, lon: -5.052, type: "Rugged" },
  { name: "Digha, WB", lat: 21.626, lon: 87.507, type: "Deltaic" },
  { name: "Rameswaram", lat: 9.287, lon: 79.312, type: "Coral" },
  { name: "Andaman Islands", lat: 11.976, lon: 92.987, type: "Mangrove" },
  { name: "Calangute, Goa", lat: 15.549, lon: 73.753, type: "Recreational" }
];

// --- MAP CONTROLLER (Handles "Flying" to locations) ---
function MapController({ onBoundsChange, targetLocation }) {
  const map = useMapEvents({
    moveend: () => onBoundsChange(map.getBounds()),
    zoomend: () => onBoundsChange(map.getBounds()),
  });

  React.useEffect(() => {
    if (targetLocation) {
      map.flyTo([targetLocation.lat, targetLocation.lon], 13, {
        animate: true,
        duration: 1.5 // Smooth flight animation
      });
    }
  }, [targetLocation, map]);

  return null;
}

// --- REPORT MODAL COMPONENT ---
const ReportModal = ({ data, location, onClose }) => {
    if (!data) return null;

    return (
        <div style={{
            position:'fixed', top:0, left:0, width:'100%', height:'100%', 
            background:'rgba(0,0,0,0.85)', zIndex:9999, display:'flex', 
            justifyContent:'center', alignItems:'center', backdropFilter:'blur(5px)'
        }}>
            <div style={{
                width:'700px', background:'#111', border:'2px solid #00d2ff', 
                borderRadius:'10px', padding:'30px', position:'relative',
                boxShadow:'0 0 30px rgba(0, 210, 255, 0.3)'
            }}>
                <button onClick={onClose} style={{position:'absolute', top:'15px', right:'20px', background:'none', border:'none', color:'white', fontSize:'20px', cursor:'pointer'}}>âœ–</button>
                
                <h2 style={{color:'#00d2ff', borderBottom:'1px solid #333', paddingBottom:'10px'}}>
                    ðŸ“„ STRATEGIC COASTAL REPORT
                </h2>
                <h4 style={{color:'white', marginTop:'5px'}}>Target Zone: {location?.name || "Unknown Region"}</h4>

                <div style={{background:'#1a1a1a', padding:'15px', borderRadius:'5px', marginBottom:'20px', borderLeft:'4px solid #00d2ff'}}>
                    <strong style={{color:'#00d2ff'}}>AI STRATEGY:</strong><br/>
                    <span style={{color:'#ddd', fontSize:'0.9em'}}>{data.ai_recommendation}</span>
                </div>

                <table style={{width:'100%', borderCollapse:'collapse', color:'#ccc', fontSize:'0.9em'}}>
                    <thead>
                        <tr style={{borderBottom:'1px solid #444', textAlign:'left'}}>
                            <th style={{padding:'10px'}}>Timeline</th>
                            <th>Vegetation (%)</th>
                            <th>Erosion Index</th>
                            <th>Recovery Potential</th>
                            <th>Status</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.timeline_data.map((row, idx) => (
                            <tr key={idx} style={{borderBottom:'1px solid #222'}}>
                                <td style={{padding:'10px', color:'white'}}>{row.year}</td>
                                <td style={{color: row.vegetation < 50 ? '#ff0055' : '#00ffcc'}}>{row.vegetation}%</td>
                                <td>{row.erosion_risk}</td>
                                <td style={{color:'#00d2ff'}}>{row.restoration_potential}</td>
                                <td style={{
                                    fontWeight:'bold', 
                                    color: row.status === 'CRITICAL' ? '#ff0055' : row.status === 'WARNING' ? '#ffcc00' : '#00ffcc'
                                }}>{row.status}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>

                <div style={{marginTop:'25px', display:'flex', justifyContent:'flex-end', gap:'10px'}}>
                    <button onClick={() => window.print()} style={{padding:'10px 20px', background:'#333', color:'white', border:'1px solid #555', cursor:'pointer'}}>Print PDF</button>
                    <button onClick={onClose} style={{padding:'10px 20px', background:'#00d2ff', color:'black', fontWeight:'bold', border:'none', cursor:'pointer'}}>Close Report</button>
                </div>
            </div>
        </div>
    );
};

// --- MAIN APPLICATION ---
function App() {
  const [currentBounds, setCurrentBounds] = useState(null);
  const [visData, setVisData] = useState(null); 
  const [reportData, setReportData] = useState(null); 
  const [showReport, setShowReport] = useState(false);
  const [loading, setLoading] = useState(false);
  const [targetLocation, setTargetLocation] = useState(null);
  const [futureYear, setFutureYear] = useState(0);

  // 1. VISUALIZATION REQUEST (Python)
  const handleAnalyze = async () => {
    if (!currentBounds) return;
    setLoading(true);
    const bbox = [currentBounds.getSouth(), currentBounds.getWest(), currentBounds.getNorth(), currentBounds.getEast()];
    
    try {
      const res = await axios.post('http://localhost:3000/api/coastal-analysis', {
        bbox, 
        future_year: futureYear, 
        show_transects: true
      });
      setVisData(res.data);
    } catch (err) { 
        console.error(err); 
        alert("Ensure Python (Port 5000) and Node (Port 3000) are running!");
    }
    setLoading(false);
  };

  // 2. REPORT GENERATION REQUEST (Python)
  const handleGenerateReport = async () => {
    setLoading(true);
    try {
        const res = await axios.post('http://localhost:3000/api/generate-report', {});
        setReportData(res.data);
        setShowReport(true);
    } catch (err) { alert("Failed to generate report"); }
    setLoading(false);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', background: '#050505', color: '#e0e0e0', fontFamily: "Arial, sans-serif" }}>
      
      {/* REPORT MODAL */}
      {showReport && <ReportModal data={reportData} location={targetLocation} onClose={() => setShowReport(false)} />}

      {/* HEADER NAV */}
      <header style={{ padding: '15px 25px', background: '#111', borderBottom: '1px solid #333', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
            <h2 style={{ margin: 0, color: '#00d2ff' }}>CoAspire <span style={{color:'white'}}>NEXUS</span></h2>
            <small style={{color:'#888'}}>Global Coastal Digital Twin</small>
        </div>
        
        {/* DROPDOWN SELECTOR FOR 25 LOCATIONS */}
        <select 
            onChange={(e) => {
                const loc = COASTAL_POINTS.find(p => p.name === e.target.value);
                setTargetLocation(loc);
                setVisData(null); // Reset visualization when moving
            }} 
            style={{padding:'10px', background:'#222', color:'white', border:'1px solid #00d2ff', borderRadius:'5px', outline:'none'}}
        >
          <option>Select Target Zone...</option>
          {COASTAL_POINTS.map(p => <option key={p.name} value={p.name}>{p.name}</option>)}
        </select>
      </header>

      <div style={{ display: 'flex', flex: 1 }}>
        
        {/* MAP PANEL */}
        <div style={{ flex: 3, position: 'relative' }}>
          <MapContainer center={[20.5937, 78.9629]} zoom={5} style={{ height: '100%', width: '100%', background:'#000' }}>
            {/* Dark Matter Map Tiles */}
            <TileLayer url="https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png" attribution='&copy; CARTO' />
            
            {/* Render all 25 Markers */}
            {COASTAL_POINTS.map((point, idx) => (
              <Marker key={idx} position={[point.lat, point.lon]}>
                <Popup style={{background:'#000'}}>
                    <strong>{point.name}</strong><br/>
                    Type: <span style={{color:'#00d2ff'}}>{point.type}</span><br/>
                    <button onClick={() => setTargetLocation(point)} style={{cursor:'pointer', marginTop:'5px'}}>Zoom Here</button>
                </Popup>
              </Marker>
            ))}

            {/* AI Layer Overlay */}
            {visData && currentBounds && (
                <ImageOverlay url={visData.mask_image} bounds={currentBounds} opacity={0.8} />
            )}
            
            {/* Logic to fly map to new coordinates */}
            <MapController onBoundsChange={setCurrentBounds} targetLocation={targetLocation} />
          </MapContainer>
        </div>

        {/* CONTROLS SIDEBAR */}
        <div style={{ flex: 1, padding: '20px', background: '#0a0a0a', borderLeft: '1px solid #333' }}>
            <h4 style={{color:'#00d2ff', borderBottom:'1px solid #333', paddingBottom:'10px'}}>CONTROL CENTER</h4>
            
            <div style={{marginBottom:'20px'}}>
                <label style={{display:'block', marginBottom:'5px', color:'#ccc'}}>Prediction Year: <span style={{color:'#ff0055'}}>+{futureYear}</span></label>
                <input 
                    type="range" min="0" max="50" step="5" 
                    value={futureYear} onChange={(e) => setFutureYear(e.target.value)} 
                    style={{width:'100%', accentColor:'#00d2ff', height:'5px'}} 
                />
            </div>
            
            <button onClick={handleAnalyze} disabled={loading} style={{
                width:'100%', padding:'12px', background:'#222', color:'#00d2ff', 
                border:'1px solid #00d2ff', marginBottom:'15px', cursor:'pointer', 
                fontWeight:'bold', textTransform:'uppercase', letterSpacing:'1px',
                opacity: loading ? 0.5 : 1
            }}>
                {loading ? 'Processing...' : 'Run Twin Simulation'}
            </button>

            <button onClick={handleGenerateReport} style={{
                width:'100%', padding:'12px', background:'linear-gradient(90deg, #00d2ff, #007bff)', 
                color:'white', border:'none', cursor:'pointer', fontWeight:'bold',
                textTransform:'uppercase', letterSpacing:'1px', borderRadius:'4px'
            }}>
                Generate Strategic Report
            </button>

            {/* Quick Stats Panel */}
            {visData && (
                <div style={{marginTop:'30px', padding:'15px', background:'#1a1a1a', border:'1px solid #333', borderRadius:'8px', animation:'fadeIn 0.5s'}}>
                    <div style={{fontSize:'0.8em', color:'#888', textTransform:'uppercase'}}>Veg Coverage</div>
                    <div style={{fontSize:'2em', color:'white', fontWeight:'bold'}}>{visData.metrics.vegetation_coverage}%</div>
                    
                    <div style={{fontSize:'0.8em', color:'#888', textTransform:'uppercase', marginTop:'10px'}}>Erosion Risk</div>
                    <div style={{fontSize:'2em', color:'#ff0055', fontWeight:'bold'}}>{visData.metrics.erosion_risk_index}</div>
                </div>
            )}
        </div>
      </div>
      
      {/* CSS Animation for fade in */}
      <style>{`@keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }`}</style>
    </div>
  );
}

export default App;
