import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, ImageOverlay, useMapEvents, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Line } from 'react-chartjs-2';

// Icon Fix
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
let DefaultIcon = L.icon({ iconUrl: icon, shadowUrl: iconShadow, iconSize: [25, 41], iconAnchor: [12, 41] });
L.Marker.prototype.options.icon = DefaultIcon;

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

function MapController({ onBoundsChange, target }) {
  const map = useMapEvents({
    moveend: () => onBoundsChange(map.getBounds()),
    zoomend: () => onBoundsChange(map.getBounds()),
  });
  useEffect(() => { 
    if (target) map.flyTo([target.lat, target.lon], 13); 
  }, [target, map]);
  return null;
}

function App() {
  const [locations, setLocations] = useState([]);
  const [selectedLoc, setSelectedLoc] = useState(null);
  const [currentBounds, setCurrentBounds] = useState(null);
  const [analysis, setAnalysis] = useState(null);
  const [history, setHistory] = useState([]);
  const [year, setYear] = useState(0);
  const [loading, setLoading] = useState(false);

  // FETCH LOCATIONS ON LOAD
  useEffect(() => {
    console.log("Fetching locations...");
    axios.get('http://localhost:3000/api/locations')
      .then(res => {
        console.log("Locations loaded:", res.data); // DEBUG LOG
        setLocations(res.data);
      })
      .catch(err => console.error("API Error:", err));
  }, []);

  const handleAnalyze = async () => {
    if (!currentBounds) return;
    setLoading(true);
    const bbox = [currentBounds.getSouth(), currentBounds.getWest(), currentBounds.getNorth(), currentBounds.getEast()];
    try {
      const res = await axios.post('http://localhost:3000/api/analyze', { 
        location_id: selectedLoc ? selectedLoc.id : null,
        bbox, 
        future_year: year 
      });
      setAnalysis(res.data);
      if (selectedLoc) fetchHistory(selectedLoc.id);
    } catch (err) { alert("Analysis Failed"); }
    setLoading(false);
  };

  const fetchHistory = (id) => {
    axios.get(`http://localhost:3000/api/history/${id}`)
      .then(res => setHistory(res.data))
      .catch(err => console.error(err));
  };

  const chartData = {
    labels: history.length > 0 ? history.map(h => new Date(h.date_analyzed).toLocaleTimeString()) : ['Current'],
    datasets: [
      {
        label: 'Veg Cover %',
        data: history.length > 0 ? history.map(h => h.vegetation_cover) : [analysis?.global_stats.vegetation_cover || 0],
        borderColor: '#4dff88',
        backgroundColor: 'rgba(77, 255, 136, 0.2)',
        fill: true,
      }
    ]
  };

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#121212', color: 'white', fontFamily: 'Arial' }}>
      
      {/* SIDEBAR */}
      <div style={{ width: '350px', padding: '20px', borderRight: '1px solid #333', display:'flex', flexDirection:'column' }}>
        <h2 style={{ color: '#00d2ff', marginTop: 0 }}>CoAspire <span style={{fontSize:'0.6em', color:'white'}}>DB</span></h2>
        
        {/* DROPDOWN */}
        <label>Target Location:</label>
        <select 
            style={{ width: '100%', padding: '10px', margin: '10px 0', background: '#333', color: 'white', border:'1px solid #555' }}
            onChange={(e) => {
                const loc = locations.find(l => l.id === parseInt(e.target.value));
                setSelectedLoc(loc);
                if(loc) fetchHistory(loc.id);
            }}
        >
            <option value="">-- Select Location ({locations.length} Loaded) --</option>
            {locations.map(l => <option key={l.id} value={l.id}>{l.name}</option>)}
        </select>

        {selectedLoc && (
            <div style={{background:'#222', padding:'10px', borderRadius:'5px', marginBottom:'20px'}}>
                <div>Region: {selectedLoc.region}</div>
                <div>Type: {selectedLoc.type}</div>
            </div>
        )}

        <label>Simulation Year (+{year})</label>
        <input type="range" min="0" max="50" value={year} onChange={e=>setYear(Number(e.target.value))} style={{width:'100%', marginBottom:'20px'}}/>
        
        <button onClick={handleAnalyze} disabled={loading} style={{ padding: '12px', background: loading ? '#555' : '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor:'pointer' }}>
            {loading ? 'Processing...' : 'Analyze & Store'}
        </button>

        {analysis && (
            <div style={{marginTop:'20px'}}>
                <h4>Live Data</h4>
                <div>Veg: <span style={{color:'#4dff88'}}>{analysis.global_stats.vegetation_cover}%</span></div>
                <div>Risk: <span style={{color:'red'}}>{analysis.global_stats.risk_status}</span></div>
            </div>
        )}
      </div>

      {/* MAP */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        <div style={{ flex: 2 }}>
            <MapContainer center={[20, 78]} zoom={4} style={{ height: '100%' }}>
                <TileLayer url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}" attribution="Esri" />
                
                {/* RENDER MARKERS FROM DB */}
                {locations.map(l => (
                    <Marker key={l.id} position={[l.lat, l.lon]}>
                        <Popup>
                            <strong>{l.name}</strong><br/>
                            <button onClick={() => { setSelectedLoc(l); fetchHistory(l.id); }}>Select</button>
                        </Popup>
                    </Marker>
                ))}
                
                {analysis && currentBounds && <ImageOverlay url={analysis.gis_image} bounds={currentBounds} opacity={0.7} />}
                <MapController onBoundsChange={setCurrentBounds} target={selectedLoc} />
            </MapContainer>
        </div>
        <div style={{ height: '200px', background: '#1a1a1a', padding: '15px' }}>
            <Line data={chartData} options={{ maintainAspectRatio: false }} />
        </div>
      </div>
    </div>
  );
}

export default App;
