function MapController({ onBoundsChange, targetLocation }) {
  const map = useMapEvents({
    moveend: () => onBoundsChange(map.getBounds()),
    zoomend: () => onBoundsChange(map.getBounds()),
  });

  // Fly to location when a point is clicked
  React.useEffect(() => {
    if (targetLocation) {
      map.flyTo([targetLocation.lat, targetLocation.lon], 13);
    }
  }, [targetLocation, map]);

  return null;
}

function App() {
  const [currentBounds, setCurrentBounds] = useState(null);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [targetLocation, setTargetLocation] = useState(null);

  // FEATURES & CONTROLS
  const [futureYear, setFutureYear] = useState(0);
  const [showTransects, setShowTransects] = useState(true);

  const handleAnalyze = async () => {
    if (!currentBounds) {
      alert("Please move the map slightly to initialize bounds.");
      return;
    }
    setLoading(true);
    
    // Get coordinates from Leaflet
    const bbox = [
        currentBounds.getSouth(), currentBounds.getWest(),
        currentBounds.getNorth(), currentBounds.getEast()
    ];

    try {
      // Call Node Backend
      const res = await axios.post('http://localhost:3000/api/coastal-analysis', {
        bbox: bbox,
        future_year: futureYear,
        show_transects: showTransects
      });
      setAnalysisResult(res.data);
    } catch (err) {
      console.error(err);
      alert("Error: Ensure Python & Node servers are running.");
    }
    setLoading(false);
  };

  const chartData = {
    labels: ['2020', '2021', '2022', '2023', `+${futureYear} Years`],
    datasets: [
      {
        label: 'Vegetation Health (NDVI)',
        data: [65, 63, 64, 62, analysisResult?.vegetation_coverage || 60],
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
      },
      {
        label: 'Coastal Erosion Impact',
        data: [20, 22, 23, 25, (25 + futureYear * 2)], // Simulated increasing erosion
        borderColor: 'rgb(255, 99, 132)',
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      }
    ],
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100vh', fontFamily: 'Arial, sans-serif', background:'#121212', color:'white' }}>
      
      {/* NAVBAR */}
      <header style={{ padding: '15px 20px', background: '#1e1e1e', borderBottom:'1px solid #333', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <h2 style={{ margin: 0, color:'#00d2ff' }}>üõ∞Ô∏è CoAspire <span style={{color:'white', fontSize:'0.7em'}}>DeepScan</span></h2>
          <small style={{color:'#888'}}>Vegetation ‚Ä¢ Waterlines ‚Ä¢ Predictive Modeling</small>
        </div>
        
        <div style={{display:'flex', gap:'15px', alignItems:'center'}}>
            <select 
                onChange={(e) => {
                    const loc = COASTAL_POINTS.find(p => p.name === e.target.value);
                    setTargetLocation(loc);
                }}
                style={{padding:'8px', background:'#333', color:'white', border:'1px solid #555', borderRadius:'4px'}}
            >
                <option>Select a Popular Location...</option>
                {COASTAL_POINTS.map(p => <option key={p.name} value={p.name}>{p.name}</option>)}
            </select>

            <button 
            onClick={handleAnalyze} 
            disabled={loading}
            style={{ 
                padding: '10px 20px', 
                background: loading ? '#555' : 'linear-gradient(90deg, #00d2ff, #007bff)', 
                color: 'white', border: 'none', borderRadius: '4px', fontWeight:'bold', cursor:'pointer' 
            }}>
            {loading ? 'Running Neural Net...' : 'Analyze Terrain'}
            </button>
        </div>
      </header>

      <div style={{ display: 'flex', flex: 1 }}>
        
        {/* MAP */}
        <div style={{ flex: 3, position: 'relative' }}>
          <MapContainer center={[20.5937, 78.9629]} zoom={5} style={{ height: '100%', width: '100%' }}>
            <TileLayer
              attribution='&copy; Esri'
              url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}"
            />
            
            {/* RENDER ALL 25 MARKERS */}
            {COASTAL_POINTS.map((point, idx) => (
                <Marker key={idx} position={[point.lat, point.lon]}>
                    <Popup>
                        <strong>{point.name}</strong><br/>
                        Region: {point.region}<br/>
                        Type: {point.type}<br/>
                        <button onClick={() => setTargetLocation(point)} style={{marginTop:'5px', cursor:'pointer'}}>Zoom Here</button>
                    </Popup>
                </Marker>
            ))}

            {/* AI OVERLAY */}
            {analysisResult && currentBounds && (
              <ImageOverlay url={analysisResult.mask_image} bounds={currentBounds} opacity={0.7} />
            )}
            
            <MapController onBoundsChange={setCurrentBounds} targetLocation={targetLocation} />
          </MapContainer>
        </div>

        {/* SIDEBAR ANALYTICS */}
        <div style={{ flex: 1, padding: '20px', background: '#1e1e1e', borderLeft: '1px solid #333', overflowY:'auto' }}>
          
          <div style={{marginBottom:'20px', padding:'15px', background:'#2a2a2a', borderRadius:'8px'}}>
            <h4 style={{marginTop:0, borderBottom:'1px solid #444', paddingBottom:'10px'}}>‚öôÔ∏è Predictive Controls</h4>
            
            <label style={{display:'block', marginBottom:'5px', fontSize:'0.9em'}}>
                Predictive Model (Years): <strong>+{futureYear}</strong>
            </label>
            <input 
                type="range" min="0" max="50" step="5" 
                value={futureYear} onChange={(e) => setFutureYear(e.target.value)}
                style={{width:'100%', accentColor:'#ff6384'}}
            />
            <small style={{color:'#aaa'}}>Simulates shoreline retreat & veg loss.</small>

            <div style={{marginTop:'15px'}}>
                <label style={{display:'flex', alignItems:'center', cursor:'pointer'}}>
                    <input type="checkbox" checked={showTransects} onChange={e => setShowTransects(e.target.checked)} style={{marginRight:'10px'}} />
                    Show Cross-shore Transects
                </label>
            </div>
          </div>

          {analysisResult ? (
            <div className="fade-in">
              <h3 style={{color:'#00d2ff'}}>Analysis Report</h3>
              
              <div style={{display:'grid', gridTemplateColumns:'1fr 1fr', gap:'10px', marginBottom:'20px'}}>
                 <div style={{background:'#333', padding:'10px', borderRadius:'5px', textAlign:'center'}}>
                    <div style={{fontSize:'1.5em', fontWeight:'bold', color:'#4bc0c0'}}>{analysisResult.vegetation_coverage}%</div>
                    <div style={{fontSize:'0.8em', color:'#ccc'}}>Vegetation</div>
                 </div>
                 <div style={{background:'#333', padding:'10px', borderRadius:'5px', textAlign:'center'}}>
                    <div style={{fontSize:'1.5em', fontWeight:'bold', color:'#ffce56'}}>{analysisResult.wave_energy_index}</div>
                    <div style={{fontSize:'0.8em', color:'#ccc'}}>Wave Energy</div>
                 </div>
              </div>

              <div style={{background:'#fff', padding:'10px', borderRadius:'5px'}}>
                <Line data={chartData} />
              </div>

              <div style={{marginTop:'15px', fontSize:'0.85em', color:'#bbb'}}>
                <strong>Auto-Extracted Features:</strong><br/>
                ‚Ä¢ Vegetation Edges (Green)<br/>
                ‚Ä¢ Wave Breaking Zone (White)<br/>
                ‚Ä¢ Transect Intersections (Yellow)<br/>
              </div>
            </div>
          ) : (
            <p style={{color:'#666', textAlign:'center', marginTop:'50px'}}>
              Select a location from the dropdown or map, then click <strong>Analyze</strong>.
            </p>
          )}

        </div>
      </div>
    </div>
  );
}

export default App;
