import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, ImageOverlay, useMapEvents, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import axios from 'axios';
import { 
  Chart as ChartJS, 
  CategoryScale, 
  LinearScale, 
  PointElement, 
  LineElement, 
  Title, 
  Tooltip, 
  Legend, 
  Filler 
} from 'chart.js';
import { Line } from 'react-chartjs-2';

// --- LEAFLET ICON FIX ---
// This prevents the "missing marker icon" issue in React Leaflet
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow,
    iconSize: [25, 41],
    iconAnchor: [12, 41]
});
L.Marker.prototype.options.icon = DefaultIcon;

// --- CHART REGISTRATION ---
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

// --- MAP CONTROLLER COMPONENT ---
// This handles flying to a location when selected from the dropdown
function MapController({ onBoundsChange, target }) {
  const map = useMapEvents({
    moveend: () => onBoundsChange(map.getBounds()),
    zoomend: () => onBoundsChange(map.getBounds()),
  });

  useEffect(() => { 
    if (target) map.flyTo([target.lat, target.lon], 13); 
  }, [target, map]);

  return null;
}

// --- MAIN APP COMPONENT ---
function App() {
  // State for Data
  const [locations, setLocations] = useState([]); // Loaded from SQLite
  const [selectedLoc, setSelectedLoc] = useState(null);
  const [currentBounds, setCurrentBounds] = useState(null);
  
  // State for Analysis
  const [analysis, setAnalysis] = useState(null);
  const [history, setHistory] = useState([]);
  
  // State for Controls
  const [year, setYear] = useState(0); // For CoastLearn
  const [loading, setLoading] = useState(false);

  // 1. Fetch Locations on Load (Connects to Node -> SQLite)
  useEffect(() => {
    axios.get('http://localhost:3000/api/locations')
      .then(res => setLocations(res.data))
      .catch(err => console.error("Database connection error. Is Node running?", err));
  }, []);

  // 2. Fetch History for Graph
  const fetchHistory = (id) => {
    axios.get(`http://localhost:3000/api/history/${id}`)
      .then(res => setHistory(res.data))
      .catch(err => console.error(err));
  };

  // 3. MAIN ANALYSIS FUNCTION
  // Connects to Node -> app.py -> (vedgesat + coastrack + coastlearn)
  const handleAnalyze = async () => {
    if (!currentBounds) {
        alert("Please wait for map to load or move map slightly.");
        return;
    }
    setLoading(true);
    
    // Get Bounding Box from Leaflet
    const bbox = [
        currentBounds.getSouth(), 
        currentBounds.getWest(), 
        currentBounds.getNorth(), 
        currentBounds.getEast()
    ];
    
    try {
      // POST request including the 'future_year' for CoastLearn
      const res = await axios.post('http://localhost:3000/api/analyze', { 
        location_id: selectedLoc ? selectedLoc.id : null,
        bbox, 
        future_year: year 
      });
      
      setAnalysis(res.data);
      
      // Refresh graph if a location is selected
      if (selectedLoc) fetchHistory(selectedLoc.id);
      
    } catch (err) { 
        console.error(err);
        alert("Analysis Failed. Ensure Python Backend is running."); 
    }
    setLoading(false);
  };

  // 4. PREPARE CHART DATA
  // Uses historical data from DB + Current Analysis
  const chartData = {
    labels: history.length > 0 ? history.map(h => new Date(h.date_analyzed).toLocaleTimeString()) : ['Current'],
    datasets: [
      {
        label: 'Vegetation Cover %',
        data: history.length > 0 ? history.map(h => h.vegetation_cover) : [analysis?.global_stats.vegetation_cover || 0],
        borderColor: '#4dff88', // Green
        backgroundColor: 'rgba(77, 255, 136, 0.2)',
        fill: true,
        tension: 0.4
      },
      {
        label: 'Beach Width (m)',
        data: history.length > 0 ? history.map(h => h.avg_beach_width) : [analysis?.global_stats.avg_width || 0],
        borderColor: '#ffff00', // Yellow
        backgroundColor: 'rgba(255, 255, 0, 0.2)',
        fill: true,
        tension: 0.4
      }
    ]
  };

  return (
    <div style={{ display: 'flex', height: '100vh', background: '#121212', color: 'white', fontFamily: 'Arial, sans-serif' }}>
      
      {/* --- LEFT SIDEBAR: CONTROLS & DATA --- */}
      <div style={{ width: '380px', padding: '20px', borderRight: '1px solid #333', display:'flex', flexDirection:'column', overflowY: 'auto' }}>
        
        {/* Title */}
        <div style={{borderBottom: '1px solid #333', paddingBottom:'15px', marginBottom:'20px'}}>
            <h2 style={{ margin: 0, color: '#00d2ff', letterSpacing:'1px' }}>
                CoAspire <span style={{fontSize:'0.6em', color:'white', opacity:0.7}}>PRO</span>
            </h2>
            <small style={{color:'#888'}}>Vegetation Edges • Waterlines • Predictive AI</small>
        </div>
        
        {/* Location Selector */}
        <label style={{fontSize:'0.85em', color:'#aaa'}}>Target Location (Database):</label>
        <select 
            style={{ width: '100%', padding: '12px', marginTop: '5px', marginBottom: '20px', background: '#252525', color: 'white', border:'1px solid #444', borderRadius:'5px' }}
            onChange={(e) => {
                const loc = locations.find(l => l.id === parseInt(e.target.value));
                setSelectedLoc(loc);
                if(loc) fetchHistory(loc.id);
            }}
        >
            <option value="">-- Manual Map Exploration --</option>
            {locations.map(l => <option key={l.id} value={l.id}>{l.name} ({l.region})</option>)}
        </select>

        {/* Predictive Control (Linked to CoastLearn) */}
        <div style={{background:'#1e1e1e', padding:'15px', borderRadius:'8px', border:'1px solid #333', marginBottom:'20px'}}>
            <label style={{display:'flex', justifyContent:'space-between', marginBottom:'10px'}}>
                <span>Simulation Year</span>
                <strong style={{color:'#ff4d4d'}}>+{year} Years</strong>
            </label>
            <input 
                type="range" min="0" max="50" step="5" 
                value={year} onChange={e=>setYear(Number(e.target.value))} 
                style={{width:'100%', accentColor:'#ff4d4d'}}
            />
            <small style={{fontSize:'0.75em', color:'#666', display:'block', marginTop:'5px'}}>
                Uses Neural Net to predict shoreline retreat.
            </small>
        </div>
        
        {/* Analyze Button */}
        <button 
            onClick={handleAnalyze} 
            disabled={loading}
            style={{ 
                padding: '15px', 
                background: loading ? '#444' : 'linear-gradient(90deg, #007bff, #00d2ff)', 
                color: 'white', 
                border: 'none', 
                borderRadius: '5px', 
                cursor: loading ? 'wait' : 'pointer', 
                fontWeight:'bold',
                fontSize:'1em',
                boxShadow: '0 4px 15px rgba(0, 210, 255, 0.2)'
            }}
        >
            {loading ? 'Processing Satellite Data...' : 'RUN ANALYSIS'}
        </button>

        {/* DATA RESULTS (Linked to CoasTrack) */}
        {analysis && (
            <div className="fade-in" style={{marginTop:'30px'}}>
                
                {/* Global Stats */}
                <div style={{display:'grid', gridTemplateColumns:'1fr 1fr', gap:'10px', marginBottom:'20px'}}>
                    <div style={{background:'#222', padding:'10px', borderRadius:'5px', textAlign:'center'}}>
                        <div style={{fontSize:'1.6em', color:'#4dff88', fontWeight:'bold'}}>{analysis.global_stats.vegetation_cover}%</div>
                        <small style={{color:'#aaa', fontSize:'0.7em'}}>Vegetation</small>
                    </div>
                    <div style={{background:'#222', padding:'10px', borderRadius:'5px', textAlign:'center'}}>
                        <div style={{fontSize:'1.6em', color:'#ffff00', fontWeight:'bold'}}>{analysis.global_stats.avg_width}m</div>
                        <small style={{color:'#aaa', fontSize:'0.7em'}}>Avg Width</small>
                    </div>
                </div>

                {/* Transect Table */}
                <h4 style={{borderBottom:'1px solid #444', paddingBottom:'5px', marginTop:0}}>Transect Data Points</h4>
                <div style={{maxHeight:'200px', overflowY:'auto'}}>
                    <table style={{width:'100%', fontSize:'0.85em', borderCollapse:'collapse'}}>
                        <thead>
                            <tr style={{color:'#888', textAlign:'left'}}>
                                <th style={{padding:'5px'}}>ID</th>
                                <th>Width</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {analysis.transect_data.map(t => (
                                <tr key={t.id} style={{borderBottom:'1px solid #333'}}>
                                    <td style={{padding:'8px'}}>{t.id}</td>
                                    <td style={{color:'#00d2ff'}}>{t.beach_width_m}m</td>
                                    <td style={{
                                        color: t.status === 'CRITICAL' ? '#ff4d4d' : 
                                               t.status === 'Warning' ? '#ffce56' : '#4dff88'
                                    }}>
                                        {t.status}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        )}
      </div>

      {/* --- RIGHT SIDE: VISUALIZATION --- */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
        
        {/* TOP: MAP PANEL */}
        <div style={{ flex: 2, position: 'relative' }}>
          <MapContainer center={[20, 78]} zoom={4} style={{ height: '100%', width: '100%', background:'#000' }}>
            
            {/* Esri Satellite Layer */}
            <TileLayer 
                url="https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}" 
                attribution="Esri" 
            />
            
            {/* Database Markers */}
            {locations.map(l => (
                <Marker key={l.id} position={[l.lat, l.lon]}>
                    <Popup>
                        <strong>{l.name}</strong><br/>
                        {l.type} Coast<br/>
                        <button onClick={() => { setSelectedLoc(l); fetchHistory(l.id); }}>Select</button>
                    </Popup>
                </Marker>
            ))}
            
            {/* AI Generated Overlay (VedgeSat Output) */}
            {analysis && currentBounds && (
                <ImageOverlay 
                    url={analysis.gis_image} 
                    bounds={currentBounds} 
                    opacity={0.7} 
                />
            )}
            
            {/* Map Controller Helper */}
            <MapController onBoundsChange={setCurrentBounds} target={selectedLoc} />

            {/* CUSTOM LEGEND (Matches vedgesat.py colors) */}
            <div style={{
                position: 'absolute', bottom: 20, right: 20, 
                background: 'rgba(0,0,0,0.8)', padding: '15px', 
                borderRadius: '8px', zIndex: 1000, fontSize: '0.85em', 
                border:'1px solid #444', backdropFilter:'blur(5px)'
            }}>
               <strong style={{color: '#fff', display:'block', marginBottom:'10px'}}>AI Interpretation</strong>
               <div style={{display: 'flex', alignItems: 'center', marginBottom: '5px'}}>
                   <span style={{width: 12, height: 12, background: '#228B22', marginRight: 10, borderRadius:'2px'}}></span> 
                   <span>Vegetation Edge</span>
               </div>
               <div style={{display: 'flex', alignItems: 'center', marginBottom: '5px'}}>
                   <span style={{width: 12, height: 12, background: '#FFFF00', marginRight: 10, borderRadius:'2px'}}></span> 
                   <span>Sand / Beach</span>
               </div>
               <div style={{display: 'flex', alignItems: 'center', marginBottom: '5px'}}>
                   <span style={{width: 12, height: 12, background: '#DFFFFF', marginRight: 10, borderRadius:'2px'}}></span> 
                   <span>Breaking Waves</span>
               </div>
               <div style={{display: 'flex', alignItems: 'center'}}>
                   <span style={{width: 12, height: 12, background: '#00008B', marginRight: 10, borderRadius:'2px'}}></span> 
                   <span>Deep Water</span>
               </div>
            </div>

          </MapContainer>
        </div>

        {/* BOTTOM: CHART PANEL */}
        <div style={{ height: '300px', background: '#181818', padding: '20px', borderTop:'1px solid #333' }}>
            <h4 style={{margin:'0 0 15px 0', color:'#ccc'}}>Historical & Predictive Analysis</h4>
            <div style={{height:'220px', width:'100%'}}>
                <Line 
                    data={chartData} 
                    options={{ 
                        maintainAspectRatio: false,
                        responsive: true,
                        scales: {
                            y: { grid: { color: '#333' }, ticks: { color: '#888' } },
                            x: { grid: { color: '#333' }, ticks: { color: '#888' } }
                        },
                        plugins: {
                            legend: { labels: { color: '#fff' } }
                        }
                    }} 
                />
            </div>
        </div>

      </div>
    </div>
  );
}

export default App;
